package revision;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age=18;
	switch(age){
		case 18:
			System.out.println("you are an adult");
			break;
		case 23:
			System.out.println("ready to join a job");
			break;
		case 40:
			System.out.println("ready to take a retairment");
			break;
		default:
			System.out.println("enjoy your life");
			break;
		}
		
		
	}

}
